draw((0,-1,0)--(0,1,0),Arrow3(6));
draw((0,0,-1)--(0,0,1),Arrow3(6));
draw(O--P--(P.x,P.y,0)--cycle);
draw(O--Q--(Q.x,Q.y,0)--cycle);
draw((0,0,0.5)..sphericaltorectangular(0.5,240,135/2)..sphericaltorectangular(0.5,240,135),Arrow3(6));
draw((0.4,0,0)..sphericaltorectangular(0.4,120,90)..sphericaltorectangular(0.4,240,90),Arrow3(6));

label("$x$", (1.1,0,0));
label("$y$", (0,1.1,0));
label("$z$", (0,0,1.1));
