pair A, B, C, D, E, F, G, H;

A = (0,0);
B = (3,0);
C = 2*dir(220);
D = (0,3);
E = (B + D)/2;
F = (A + B)/2;
G = (A + C)/2;
H = (C + D)/2;

draw(A--B,dashed);
draw(A--C,dashed);
draw(A--D,dashed);
draw(B--C--D--cycle);
draw(E--F--G--H--cycle);

label("$A$", A, NE);
label("$B$", B, dir(0));
label("$C$", C, SW);
label("$D$", D, N);
label("$E$", E, NE);
label("$F$", F, NE);
