        ("1", "Linear"),
        ("2", "Quadratic"),
        ("3", "Cubic"),
        ("4", "Quartic"),
        ("5", "Quintic"),
        ("6", "Sextic"),
        ("7", "Septic"),
        ("8", "Octic"),
        ("9", "Nonic"),
        ("10", "Dionic"),
        ("*", "Polydegree")
    ]
}

# Create the polynomial class
class PolyClass(Poly):
    def __new__(cls, *args, **kwargs):
        # Process the domain option
        domain = options["domain"][kwargs.pop("domain", 0)]
        if domain == "*":
            degree = kwargs.pop("degree", 1)
            if degree == 1:
                domain = "ZZ"
            elif degree == 2:
                domain = "QQ"
            elif degree == 3:
                domain = "FF"
            elif degree == 4:
                domain = "CC"
            else:
                raise ValueError(f"Invalid polydegree: {degree}")
        elif degree in [1, 2, 3, 4]:
            domain = "QQ"  # Assume QQ for specific degree
        elif degree == 5:
            domain = "FF"
        elif degree == 6:
            domain = "CC"
        elif degree == 7:
            domain = "QQ"  # Assume QQ for septic
        elif degree == 8:
            domain = "FF"  # Assume FF for octic
        elif degree == 9:
            domain = "CC"  # Assume CC for nonic
        elif degree == 10:
            domain = "QQ"  # Assume Q

