void SVGPathSeg::addToPath(SVGPath* path, const SVGPoint& currentPoint) const
{
    if (m_type == SVGPathSegMovetoAbs || m_type == SVGPathSegMovetoRel) {
        path->moveTo(currentPoint.x() + m_x, currentPoint.y() + m_y);
    } else if (m_type == SVGPathSegLinetoAbs || m_type == SVGPathSegLinetoRel) {
        path->lineTo(currentPoint.x() + m_x, currentPoint.y() + m_y);
    } else if (m_type == SVGPathSegCurvetoQuadraticAbs || m_type == SVGPathSegCurvetoQuadraticRel) {
        path->quadraticCurveTo(currentPoint.x() + m_x, currentPoint.y() + m_y, 2 * m_x, 2 * m_y);
    } else if (m_type == SVGPathSegCurvetoCubicAbs || m_type == SVGPathSegCurvetoCubicRel) {
        path->curveTo(currentPoint.x() + m_x, currentPoint.y() + m_y, 3 * m_x, 3 * m_y, 4 * m_x, 4 * m_y);
    } else if (m_type == SVGPathSegArcAbs || m_type == SVGPathSegArcRel) {
        path->addArc(currentPoint.x() + m_x, currentPoint.y() + m_y, m_x == 0 ? 0 : m_x, m_y == 0 ? 0 : m_y, m_largeArcFlag, m_sweepFlag, m_x * 360 / (2 * 3.141593), m_y * 360 /

(2 * 3.141593));
    } else if (m_type == SVGPathSegClosePath) {
        path->closePath();
    }
}

