{
}

/*--------------------------------------------------------------------
 * VRT_Table
 */
struct vrt_table *vrt_table_create(const char *name, int (*call)(struct vrt_ctx *),
                                  int (*next)(struct vrt_ctx *),
                                  int (*done)(struct vrt_ctx *),
                                  void *priv, void (*alloc)(void *),
                                  void (*free)(void *))
{
    struct vrt_table *table;

    table = calloc(1, sizeof(*table));
    if (table == NULL) {
        return NULL;
    }

    table->name = strdup(name);
    if (table->name == NULL) {
        free(table);
        return NULL;
    }

    table->priv = priv;
    table->alloc = alloc;
    table->free = free;

    table->call = call;
    table->next = next;
    table->done = done;

    return table;
}

/*--------------------------------------------------------------------
 * VRT_Destroy
 */
void vrt_table_destroy(struct vrt_table *table)
{
    if (table == NULL) {
        return;
    }

    free(table->name);
    table->alloc(table->priv);
    free(table);
}

/*--------------------------------------------------------------------
 * VRT_Insert
 */
int vrt_table_insert(struct vrt_

main *main, struct vrt_table *table)
{
    int i;

    for (i = 0; i < main->count; i++) {
        if (strcmp(main->tables[i]->name, table->name) == 0) {
            return VRT_ERR_TABLE_EXISTS;
        }
    }

    main->count++;
    main->tables = realloc(main->tables, sizeof(*main->tables) * main->count);
    if (main->tables == NULL) {
        return VRT_ERR_NOMEM;
    }

    main->tables[main->count - 1] = table;
    return 0;
}

/*--------------------------------------------------------------------
 * VRT_Lookup
 */
struct vrt_table *vrt_table_lookup(struct vrt_main *main, const char *name)
{
    int i;

    for (i = 0; i < main->count; i++) {
        if (strcmp(main->tables[i]->name, name) == 0) {
            return main->tables[i];
        }
    }

    return NULL;
}

/*--------------------------------------------------------------------
 * VRT_Default
 */
struct vrt_table *vrt_default_table(struct vrt_main *main)
{
    return vrt_table_lookup(main, "default");
}

