# Function to find the prime factorization of a number n
def prime_factors(n):
    factors = {}
    # Count the number of times 2 divides n
    while n % 2 == 0:
        if 2 not in factors:
            factors[2] = 0
        factors[2] += 1
        n = n // 2
    # n must be odd at this point, so we can skip one element (Note i = i +2)
    for i in range(3, int(n**0.5) + 1, 2):
        # While i divides n, count i and divide n
        while n % i == 0:
            if i not in factors:
                factors[i] = 0
            factors[i] += 1
            n = n // i
    # If n is a prime greater than 2
    if n > 2:
        factors[n] = 1
    return factors

# Find the prime factorization of 2400
prime_factors_2400 = prime_factors(2400)
